    test('Return correct imageIndex based on windowWidth', async () => {
        const wrapper = shallowMount(SearchResults, { store, localVue });
        await wrapper.setData({ windowWidth: 768 })
        expect(wrapper.vm.imageIndex).toBe(2);
        await wrapper.setData({ windowWidth: 1280 })
        expect(wrapper.vm.imageIndex).toBe(1);
    })

    test('Toggle results visibility', async () => {
        const wrapper = shallowMount(SearchResults, { store, localVue });
        await store.commit('TOGGLE_SHOW_RESULTS');
        expect(state.showResults).toBe(true);
        await store.commit('TOGGLE_SHOW_RESULTS');
        expect(state.showResults).toBe(false);
    })

